mapper.select:
SELECT user_No as userNo, user_Id as userId, user_Pwd as userPwd, user_Name as userName, user_Email as userEmail, gender as gender, nickname as nickname, enroll_Date as enrollDate, roll as roll, end_YN as endYN FROM (SELECT ROW_NUMBER() OVER(ORDER BY USER_NO ASC) AS NUM, MEMBER.* FROM MEMBER WHERE
USER_ID LIKE '%'||#{keyword}||'%'
NICKNAME Like '%'||#{keyword}||'%'
) WHERE NUM BETWEEN #{start} AND #{end}

SELECT notice_No as noticeNo, title as title, content as content, user_Id as userId, regdate as regdate, end_YN as endYN, user_name as username FROM (SELECT ROW_NUMBER() OVER(ORDER BY NOTICE_NO DESC) AS NUM, N.*, USER_NAME FROM NOTICE N LEFT JOIN MEMBER M ON (M.USER_ID=N.USER_ID) WHERE
TITLE LIKE '%'||#{keyword}||'%'
CONTENT Like '%'||#{keyword}||'%'
) WHERE NUM BETWEEN #{start} AND #{end}

SELECT faq_no as faqNo, category as category, title as title, content as content, user_id as userId, regdate as regdate, end_yn as endYN, user_name as userName FROM (SELECT ROW_NUMBER() OVER(ORDER BY FAQ_NO DESC)AS NUM, F.*, USER_NAME FROM FAQ F LEFT JOIN MEMBER M ON (F.USER_ID=M.USER_ID) WHERE
CATEGORY=#{category} AND (TITLE LIKE '%'||#{keyword}||'%' OR CONTENT LIKE '%'||#{keyword}||'%')
CATEGORY=#{category} AND (TITLE LIKE '%'||#{keyword}||'%' OR CONTENT LIKE '%'||#{keyword}||'%')
CATEGORY=#{category} AND (TITLE LIKE '%'||#{keyword}||'%' OR CONTENT LIKE '%'||#{keyword}||'%')
) WHERE NUM BETWEEN #{start} AND #{end}
